import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./BlogDetail.css";

function BlogDetail() {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5000/api/blog/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setBlog(data);
        setLoading(false);
      })
      .catch((err) => console.error("Lỗi tải bài viết:", err));
  }, [id]);

  if (loading) return <p>Đang tải bài viết...</p>;
  if (!blog) return <p>Không tìm thấy bài viết</p>;

  return (
    <div className="blog-detail-container">
      <h1 className="blog-detail-title">{blog.title}</h1>

      {blog.thumbnail && (
        <img
          src={`http://localhost:5000/uploads/blogs/${blog.thumbnail}`}
          alt="Thumbnail"
          className="blog-thumbnail"
        />
      )}

      <div className="blog-info">
        <span>Tác giả: {blog.author || "Admin"}</span>
        <span>
          Ngày đăng: {new Date(blog.createdAt).toLocaleDateString("vi-VN")}
        </span>
      </div>

      <div className="blog-content">{blog.content}</div>

      <h2 className="comment-title">Bình luận</h2>

      {/* comment sẽ làm sau */}
      <p>(Chức năng bình luận đang phát triển)</p>
    </div>
  );
}

export default BlogDetail;
