import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import CommentList from "../../components/Comment/CommentList";
import "./BlogDetail.css";

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  // H√†m load l·∫°i blog (bao g·ªìm comments)
  const fetchBlog = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/blog/${id}`);
      const data = await res.json();
      setBlog(data);
      setLoading(false);
    } catch (error) {
      console.error("L·ªói t·∫£i b√†i vi·∫øt:", error);
    }
  };

  useEffect(() => {
    fetchBlog();
  }, [id]);

  // Like / Dislike blog
  const handleBlogReaction = async (type) => {
    if (!token) return alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p!");

    await fetch(`http://localhost:5000/api/blog/${id}/reaction`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ type }),
    });

    fetchBlog(); // reload blog sau khi reaction
  };

  if (loading) return <p>ƒêang t·∫£i b√†i vi·∫øt...</p>;
  if (!blog) return <p>Kh√¥ng t√¨m th·∫•y b√†i vi·∫øt</p>;

  return (
    <div className="blog-detail-container">
      <h1 className="blog-title">{blog.title}</h1>

      {blog.thumbnail && (
        <img
          src={`http://localhost:5000/uploads/blogs/${blog.thumbnail}`}
          alt="Thumbnail"
          className="blog-thumbnail"
        />
      )}

      <div className="blog-meta">
        <span>T√°c gi·∫£: {blog.author || "Admin"}</span>
        <span>
          Ng√†y ƒëƒÉng: {new Date(blog.createdAt).toLocaleDateString("vi-VN")}
        </span>
      </div>

      <div className="blog-content">{blog.content}</div>

      <div className="blog-reaction">
        <button onClick={() => handleBlogReaction("like")}>
          üëç {blog.likes}
        </button>
        <button onClick={() => handleBlogReaction("dislike")}>
          üëé {blog.dislikes}
        </button>
      </div>

      <h2>B√¨nh lu·∫≠n</h2>

      {/* TRUY·ªÄN comments AN TO√ÄN */}
      <CommentList
        comments={blog.comments || []}
        blogId={id}
        reload={fetchBlog}
      />
    </div>
  );
};

export default BlogDetail;
