import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import CommentList from "./CommentList";
import "./Comment.css";

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [comment, setComment] = useState("");
  const currentUser = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  // Load blog + comments
  const loadData = () => {
    fetch(`http://localhost:5000/api/blog/${id}`)
      .then((res) => res.json())
      .then((data) => setBlog(data));
  };

  useEffect(() => {
    loadData();
  }, [id]);

  // Add new comment
  const handleAddComment = async () => {
    if (!comment.trim()) return;

    await fetch(`http://localhost:5000/api/blog/${id}/comment`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ content: comment }),
    });

    setComment("");
    loadData();
  };

  // Reply comment
  const handleReply = async (parentId, text) => {
    if (!text.trim()) return;

    await fetch(`http://localhost:5000/api/blog/${id}/comment/${parentId}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ content: text }),
    });

    loadData();
  };

  // Edit comment
  const handleEdit = async (commentId, newContent) => {
    await fetch(`http://localhost:5000/api/blog/comment/${commentId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ content: newContent }),
    });

    loadData();
  };

  // Delete comment
  const handleDelete = async (commentId) => {
    await fetch(`http://localhost:5000/api/blog/comment/${commentId}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });

    loadData();
  };

  if (!blog) return <p>Đang tải...</p>;

  return (
    <div className="blog-detail">
      <h1>{blog.title}</h1>

      {blog.thumbnail && (
        <img
          src={`http://localhost:5000/uploads/blogs/${blog.thumbnail}`}
          alt="thumbnail"
          className="blog-thumb"
        />
      )}

      <p className="blog-content">{blog.content}</p>

      <hr />

      <h2>Bình luận</h2>

      {/* Comment input */}
      {token ? (
        <div className="comment-input">
          <img
            src={`http://localhost:5000/uploads/avatars/${currentUser?.avatar}`}
            className="comment-avatar"
            alt=""
          />
          <textarea
            placeholder="Viết bình luận..."
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          ></textarea>
          <button onClick={handleAddComment}>Gửi</button>
        </div>
      ) : (
        <p>Bạn cần đăng nhập để bình luận.</p>
      )}

      {/* Comment list */}
      <CommentList
        comments={blog.comments}
        onReply={handleReply}
        onEdit={handleEdit}
        onDelete={handleDelete}
        currentUser={currentUser}
      />
    </div>
  );
};

export default BlogDetail;
