import React from "react";
import CommentItem from "./CommentItem";

const CommentList = ({ comments, blogId, reload }) => {
  return (
    <div>
      {comments.map((c) => (
        <CommentItem
          key={c.id}
          comment={c}
          blogId={blogId}
          reload={reload} // <<< TRUYỀN CHUẨN
        />
      ))}
    </div>
  );
};

export default CommentList;
