import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./BlogDetail.css";

const API = "http://localhost:5000";

export default function BlogDetail() {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`${API}/api/blog/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setBlog(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error loading blog:", err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p className="detail-loading">ƒêang t·∫£i b√†i vi·∫øt...</p>;
  if (!blog) return <p className="detail-error">Kh√¥ng t√¨m th·∫•y b√†i vi·∫øt</p>;

  return (
    <div className="blogdetail-container">
      <h1 className="blogdetail-title">{blog.title}</h1>

      <div className="blogdetail-meta">
        <span>‚úè {blog.author}</span>
        <span>üìÖ {new Date(blog.createdAt).toLocaleDateString()}</span>
      </div>

      {blog.thumbnail && (
        <div className="blogdetail-thumbnail">
          <img
            src={`${API}/uploads/blogs/${blog.thumbnail}`}
            alt={blog.title}
          />
        </div>
      )}

      <div
        className="blogdetail-content"
        dangerouslySetInnerHTML={{ __html: blog.content }}
      ></div>

      {/* Comment section s·∫Ω l√†m ·ªü b∆∞·ªõc ti·∫øp theo */}
      <div className="blogdetail-comments">
        <h2>B√¨nh lu·∫≠n</h2>
        <p>(S·∫Ω l√†m b∆∞·ªõc ti·∫øp theo)</p>
      </div>
    </div>
  );
}
