import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./Register.css";

function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logic xử lý đăng ký (gửi dữ liệu tới API) sẽ được thêm vào đây
    console.log("Đăng ký với:", { name, email, password });
    alert(`Đăng ký thành công (giả lập) cho người dùng: ${name}`);
    // Sau khi đăng ký thành công, có thể chuyển hướng người dùng đến trang đăng nhập
  };

  return (
    <div className="register-page">
      <div className="register-container">
        <h2 className="register-title">Tạo Tài Khoản Mới</h2>
        <form className="register-form" onSubmit={handleSubmit}>
          {/* Trường Tên người dùng */}
          <div className="form-group">
            <label htmlFor="name">Tên người dùng</label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Nhập tên của bạn"
              required
            />
          </div>

          {/* Trường Email */}
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Nhập email hợp lệ"
              required
            />
          </div>

          {/* Trường Mật khẩu */}
          <div className="form-group">
            <label htmlFor="password">Mật khẩu</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Tối thiểu 6 ký tự"
              required
              minLength="6"
            />
          </div>

          <button type="submit" className="register-button">
            Đăng ký
          </button>
        </form>

        <p className="login-link">
          Đã có tài khoản? <NavLink to="/login">Đăng nhập</NavLink>
        </p>
      </div>
    </div>
  );
}

export default Register;
