import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Blog.css";

const API = "http://localhost:5000";

export default function BlogList() {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    fetch(`${API}/api/blog`)
      .then((res) => res.json())
      .then((data) => {
        console.log("Blogs fetched:", data);
        setBlogs(data);
      })
      .catch((err) => console.error("Fetch blog error:", err));
  }, []);

  return (
    <div className="bloglist-wrapper">
      <h1 className="bloglist-title">üìö B√†i vi·∫øt m·ªõi nh·∫•t</h1>

      <div className="bloglist-grid">
        {blogs.length === 0 ? (
          <p className="bloglist-empty">Ch∆∞a c√≥ b√†i vi·∫øt n√†o</p>
        ) : (
          blogs.map((blog) => (
            <Link
              to={`/blog/${blog.id}`}
              key={blog.id}
              className="bloglist-card"
            >
              <div className="bloglist-thumbnail">
                {blog.thumbnail ? (
                  <img
                    src={`${API}/uploads/blogs/${blog.thumbnail}`}
                    alt={blog.title}
                  />
                ) : (
                  <div className="bloglist-noimage">Kh√¥ng c√≥ ·∫£nh</div>
                )}
              </div>

              <div className="bloglist-info">
                <h2 className="bloglist-post-title">{blog.title}</h2>
                <p className="bloglist-author">‚úè {blog.author}</p>
                <p className="bloglist-date">
                  üìÖ {new Date(blog.createdAt).toLocaleDateString()}
                </p>
              </div>
            </Link>
          ))
        )}
      </div>
    </div>
  );
}
