import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [commentText, setCommentText] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetch(`http://localhost:5000/api/blog/${id}`)
      .then((res) => res.json())
      .then((data) => setBlog(data))
      .catch((err) => console.error(err));
  }, [id]);

  const handleAddComment = async () => {
    if (!commentText.trim()) return;

    const token = localStorage.getItem("token");
    if (!token) {
      setMessage("Bạn cần đăng nhập để bình luận.");
      return;
    }

    try {
      const res = await fetch(`http://localhost:5000/api/blog/${id}/comment`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ content: commentText }),
      });

      const data = await res.json();

      if (data.error) {
        setMessage(data.error);
      } else {
        setMessage("Đã gửi bình luận!");
        setCommentText("");

        // Reload blog + comment list
        fetch(`http://localhost:5000/api/blog/${id}`)
          .then((res) => res.json())
          .then((data) => setBlog(data));
      }
    } catch (error) {
      console.log(error);
    }
  };

  if (!blog) return <p>Đang tải bài viết...</p>;

  return (
    <div className="blog-detail-container">
      <h1>{blog.title}</h1>

      {blog.thumbnail && (
        <img
          src={`http://localhost:5000/uploads/blogs/${blog.thumbnail}`}
          alt="Thumbnail"
          className="blog-thumb"
        />
      )}

      <p className="blog-content">{blog.content}</p>

      <hr />

      <h3>Bình luận</h3>

      {message && <p style={{ color: "red" }}>{message}</p>}

      <div className="comment-box">
        <textarea
          placeholder="Viết bình luận..."
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
        ></textarea>

        <button onClick={handleAddComment}>Gửi bình luận</button>
      </div>

      <ul className="comment-list">
        {blog.comments?.map((c) => (
          <li key={c.id} className="comment-item">
            <img
              src={`http://localhost:5000/uploads/avatars/${c.avatar}`}
              className="comment-avatar"
              alt="avatar"
            />
            <div>
              <strong>{c.userName}</strong>
              <p>{c.content}</p>
              <small>{new Date(c.createdAt).toLocaleString()}</small>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BlogDetail;
