import React, { useState } from "react";
import "./WasteClassification.css";

function WasteClassification() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      setSelectedImage(file);
      setPreview(URL.createObjectURL(file));
      setResult(null);
    }
  };

  const handleReset = () => {
    setSelectedImage(null);
    setPreview(null);
    setResult(null);
    setLoading(false);
  };

  const handlePredict = async () => {
    if (!selectedImage) return;

    setLoading(true);
    setResult(null);

    const formData = new FormData();
    formData.append("image", selectedImage);

    try {
      const API_URL = "http://localhost:5000/api/predict";

      const response = await fetch(API_URL, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Lỗi dự đoán từ Server.");
      }

      const data = await response.json();
      setResult(data.prediction);
    } catch (error) {
      console.error("Lỗi dự đoán:", error);
      setResult({
        label: `Lỗi Server: ${error.message || "Không xác định"}`,
        confidence: 0,
      });
    } finally {
      setLoading(false);
    }
  };

  const formatConfidence = (conf) => {
    if (typeof conf !== "number") return "0%";
    const safeValue = Math.max(0, Math.min(conf, 1));
    return `${(safeValue * 100).toFixed(1)}%`;
  };

  const confidenceValue =
    typeof result?.confidence === "number" ? result.confidence : 0;
  const confidencePercent = Math.max(0, Math.min(confidenceValue, 1)) * 100;

  return (
    <div className="waste-classification">
      <section className="wc-hero card">
        <div className="wc-hero__meta">Trợ lý phân loại</div>
        <h1>Phân loại rác bằng AI</h1>
        <p>
          Tải ảnh rác để hệ thống phân tích và gợi ý loại rác phù hợp. Kết quả
          hiển thị kèm độ tin cậy để bạn quyết định xử lý.
        </p>
        <div className="wc-hero__tags">
          <span className="wc-chip">Ảnh rõ nét</span>
          <span className="wc-chip">Phân tích ngay</span>
          <span className="wc-chip">Theo dõi độ tin cậy</span>
        </div>
      </section>

      <div className="wc-grid">
        <section className="wc-card card">
          <div className="wc-card__head">
            <span className="wc-badge">Bước 1</span>
            <div>
              <h2 className="wc-title">Chọn ảnh cần phân loại</h2>
              <p className="wc-subtitle">
                Kéo thả hoặc tải ảnh rác từ máy tính. Giữ ảnh đủ sáng, không quá
                mờ để AI nhận diện chính xác.
              </p>
            </div>
          </div>

          <label
            className={`wc-dropzone ${selectedImage ? "has-file" : ""}`}
            htmlFor="wc-upload"
          >
            <input
              id="wc-upload"
              type="file"
              accept="image/*"
              onChange={handleImageChange}
            />
            <div className="wc-dropzone__icon">AI</div>
            <div>
              <p className="wc-dropzone__title">
                {selectedImage ? "Ảnh đã sẵn sàng" : "Kéo thả hoặc bấm để chọn"}
              </p>
              <p className="wc-dropzone__hint">
                Hỗ trợ PNG, JPG, JPEG. Nên dùng ảnh <strong>&lt; 5MB</strong>
              </p>
              {selectedImage && (
                <p className="wc-file-name">{selectedImage.name}</p>
              )}
            </div>
          </label>

          {preview && (
            <div className="wc-preview">
              <div className="wc-preview__meta">
                <span>Xem nhanh</span>
                <span>{formatConfidence(confidenceValue)}</span>
              </div>
              <img src={preview} alt="Xem trước" />
            </div>
          )}

          <div className="wc-actions">
            <button
              className="wc-btn wc-btn--primary"
              onClick={handlePredict}
              disabled={!selectedImage || loading}
            >
              {loading ? "Đang phân tích..." : "Phân loại ngay"}
            </button>
            <button
              className="wc-btn wc-btn--ghost"
              onClick={handleReset}
              disabled={!selectedImage && !preview && !result && !loading}
            >
              Đặt lại
            </button>
          </div>

          <ul className="wc-tips">
            <li>Giữ vật thể chính chiếm phần lớn khung hình.</li>
            <li>Tránh chói sáng, ảnh quá tối hoặc bị mờ.</li>
            <li>
              Thử nhiều góc chụp nếu kết quả chưa khớp với thực tế của bạn.
            </li>
          </ul>
        </section>

        <section className="wc-card card">
          <div className="wc-card__head">
            <span className="wc-badge">Bước 2</span>
            <div>
              <h2 className="wc-title">Xem kết quả phân loại</h2>
              <p className="wc-subtitle">
                Kết quả hiển thị ngay sau khi AI xử lý. Độ tin cậy giúp bạn tự
                kiểm chứng nhanh.
              </p>
            </div>
          </div>

          <div className="wc-status">
            <span
              className={`wc-chip ${
                loading ? "wc-chip--pulse" : result ? "wc-chip--success" : ""
              }`}
            >
              {loading
                ? "Đang xử lý..."
                : result
                ? "Hoàn tất nhận diện"
                : "Chờ ảnh tải lên"}
            </span>
          </div>

          {result ? (
            <div className="wc-result">
              <div className="wc-result__label">{result.label}</div>
              <div className="wc-confidence">
                <span>Độ tin cậy</span>
                <div className="wc-progress">
                  <div
                    className="wc-progress__bar"
                    style={{ width: `${confidencePercent}%` }}
                  />
                </div>
                <span className="wc-confidence__value">
                  {formatConfidence(confidenceValue)}
                </span>
              </div>
              <p className="wc-result__hint">
                Nếu kết quả chưa hợp lý, hãy thử chụp cận hơn hoặc chọn ảnh khác
                để cải thiện độ tin cậy.
              </p>
            </div>
          ) : (
            <div className="wc-placeholder">
              <p>Tải ảnh lên để xem kết quả phân loại và độ tin cậy.</p>
              <p className="wc-placeholder__note">
                Mẹo: ảnh rõ nét, nền đơn giản sẽ cho kết quả chuẩn hơn.
              </p>
            </div>
          )}

          <div className="wc-helper-grid">
            <div className="wc-helper">
              <h4>Ảnh chụp trực tiếp</h4>
              <p>Chụp từ camera điện thoại hoặc laptop, giữ vật thể ở trung tâm.</p>
            </div>
            <div className="wc-helper">
              <h4>Ảnh sẵn có</h4>
              <p>Chọn ảnh trong thư viện để so sánh nhanh các loại rác tương tự.</p>
            </div>
            <div className="wc-helper">
              <h4>Ghi chú nhỏ</h4>
              <p>Đọc kỹ hướng dẫn tái chế tại địa phương sau khi nhận được kết quả.</p>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
}

export default WasteClassification;
