import React, { useState, useEffect } from "react";
import "./CreateBlog.css";

const CreateBlog = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [thumbnail, setThumbnail] = useState(null);
  const [preview, setPreview] = useState(null);
  const [message, setMessage] = useState("");

  // Ki·ªÉm tra role admin
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user || user.role !== "admin") {
      setMessage("B·∫°n kh√¥ng c√≥ quy·ªÅn ƒëƒÉng b√†i!");
    }
  }, []);

  const handleThumbnailChange = (e) => {
    const file = e.target.files[0];
    setThumbnail(file);

    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title.trim() || !content.trim()) {
      setMessage("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin!");
      return;
    }

    const token = localStorage.getItem("token");
    if (!token) {
      setMessage("B·∫°n ch∆∞a ƒëƒÉng nh·∫≠p!");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    if (thumbnail) formData.append("thumbnail", thumbnail);

    try {
      const response = await fetch("http://localhost:5000/api/blog", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      const data = await response.json();

      if (data.error) {
        setMessage("L·ªói: " + data.error);
      } else {
        setMessage("ƒêƒÉng b√†i th√†nh c√¥ng!");
        setTitle("");
        setContent("");
        setThumbnail(null);
        setPreview(null);
      }
    } catch (error) {
      console.error("Error:", error);
      setMessage("L·ªói k·∫øt n·ªëi server.");
    }
  };

  return (
    <div className="create-blog-container">
      <h2>üìù T·∫°o b√†i vi·∫øt m·ªõi</h2>

      {message && <p className="message">{message}</p>}

      <form onSubmit={handleSubmit} className="create-blog-form">
        <label>Ti√™u ƒë·ªÅ b√†i vi·∫øt:</label>
        <input
          type="text"
          placeholder="Nh·∫≠p ti√™u ƒë·ªÅ..."
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />

        <label>N·ªôi dung:</label>
        <textarea
          placeholder="Nh·∫≠p n·ªôi dung b√†i vi·∫øt..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows={6}
        ></textarea>

        <label>Thumbnail:</label>
        <input type="file" accept="image/*" onChange={handleThumbnailChange} />

        {preview && (
          <div className="preview-box">
            <p>·∫¢nh xem tr∆∞·ªõc:</p>
            <img src={preview} alt="preview" />
          </div>
        )}

        <button type="submit" className="btn-submit">
          ƒêƒÉng b√†i
        </button>
      </form>
    </div>
  );
};

export default CreateBlog;
