import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import CommentList from "../../components/Blog/CommentList";
import "./BlogDetail.css";

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  // Fetch blog + comments + like/dislike
  const fetchBlog = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/blog/${id}`);
      const data = await res.json();
      setBlog(data);
      setLoading(false);
    } catch (error) {
      console.error("L·ªói fetch chi ti·∫øt blog:", error);
    }
  };

  // Reaction blog
  const handleBlogReaction = async (type) => {
    if (!token) {
      alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ thao t√°c!");
      return;
    }

    await fetch(`http://localhost:5000/api/blog/${id}/reaction`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ type }),
    });

    fetchBlog(); // c·∫≠p nh·∫≠t l·∫°i like/dislike
  };

  useEffect(() => {
    fetchBlog();
  }, []);

  if (loading) return <p>ƒêang t·∫£i...</p>;
  if (!blog) return <p>Kh√¥ng t√¨m th·∫•y b√†i vi·∫øt</p>;

  return (
    <div className="blog-detail-container">
      {/* Ti√™u ƒë·ªÅ */}
      <h1 className="blog-detail-title">{blog.title}</h1>

      {/* T√°c gi·∫£ */}
      <p className="blog-author">Vi·∫øt b·ªüi: {blog.author}</p>

      {/* ·∫¢nh thumbnail */}
      {blog.thumbnail && (
        <img
          src={`http://localhost:5000/uploads/blogs/${blog.thumbnail}`}
          alt="Thumbnail"
          className="blog-thumbnail"
        />
      )}

      {/* N·ªôi dung */}
      <div className="blog-content">{blog.content}</div>

      {/* -------------------- LIKE / DISLIKE BLOG -------------------- */}
      <div className="blog-reaction-box">
        <button
          className="react-btn like-btn"
          onClick={() => handleBlogReaction("like")}
        >
          üëç {blog.likes}
        </button>

        <button
          className="react-btn dislike-btn"
          onClick={() => handleBlogReaction("dislike")}
        >
          üëé {blog.dislikes}
        </button>
      </div>

      {/* -------------------- COMMENT LIST -------------------- */}
      <h2 className="comment-title">B√¨nh lu·∫≠n</h2>

      <CommentList comments={blog.comments} blogId={id} reload={fetchBlog} />
    </div>
  );
};

export default BlogDetail;
