import React from "react";
import { ArrowRight } from "lucide-react";

// --- CSS Styles (Tích hợp vào JS để chạy demo) ---
const styles = `
/* --- Cấu trúc chung --- */
.blog-section {
  padding: 4rem 1rem;
  background-color: #f8fafc;
  font-family: 'Quicksand', sans-serif;
}

.blog-container {
  max-width: 72rem;
  margin: 0 auto;
}

/* --- Header của Section --- */
.blog-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-end;
  margin-bottom: 2.5rem;
}

.section-title {
  font-size: 1.875rem;
  font-weight: 700;
  color: #1f2937;
  margin: 0;
}

.section-subtitle {
  color: #6b7280;
  margin-top: 0.5rem;
}

.view-all-link {
  display: none;
  color: #16a34a;
  font-weight: 600;
  text-decoration: none;
  align-items: center;
  gap: 0.25rem;
}

.view-all-link:hover {
  text-decoration: underline;
}

@media (min-width: 768px) {
  .view-all-link {
    display: flex;
  }
}

/* --- Grid Layout --- */
.blog-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 2rem;
}

@media (min-width: 768px) {
  .blog-grid {
    grid-template-columns: repeat(3, 1fr);
  }
}

/* --- Blog Card --- */
.blog-card {
  background-color: white;
  border-radius: 0.75rem;
  overflow: hidden;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  transition: box-shadow 0.3s ease, transform 0.3s ease;
  cursor: pointer;
  display: flex;
  flex-direction: column;
}

.blog-card:hover {
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
  transform: translateY(-2px);
}

/* --- Hình ảnh --- */
.blog-image-wrapper {
  height: 12rem;
  overflow: hidden;
}

.blog-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.5s ease;
}

.blog-card:hover .blog-image {
  transform: scale(1.1);
}

/* --- Nội dung bài viết --- */
.blog-content {
  padding: 1.5rem;
  display: flex;
  flex-direction: column;
  flex-grow: 1;
}

/* Tags */
.blog-tag {
  font-size: 0.75rem;
  font-weight: 700;
  padding: 0.25rem 0.5rem;
  border-radius: 0.25rem;
  margin-bottom: 0.75rem;
  display: inline-block;
  width: fit-content;
}

.tag-news {
  color: #16a34a;
  background-color: #dcfce7;
}

.tag-tips {
  color: #2563eb;
  background-color: #dbeafe;
}

.tag-view {
  color: #ea580c;
  background-color: #ffedd5;
}

/* Tiêu đề bài viết */
.blog-title {
  font-size: 1.125rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
  line-height: 1.5;
}

.blog-title a {
  text-decoration: none;
  color: inherit;
  transition: color 0.2s;
}

.blog-card:hover .blog-title a {
  color: #16a34a;
}

/* Đoạn trích */
.blog-excerpt {
  font-size: 0.875rem;
  color: #6b7280;
  margin-bottom: 1rem;
  line-height: 1.5;
}

/* Nút đọc thêm */
.read-more-btn {
  margin-top: auto;
  font-size: 0.875rem;
  font-weight: 600;
  color: #374151;
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 0.25rem;
  transition: color 0.2s;
}

.blog-card:hover .read-more-btn {
  color: #16a34a;
}
`;

// Dữ liệu mẫu cho Blog (Bạn có thể chuyển thành props hoặc fetch từ API)
const BLOG_DATA = [
  {
    id: 1,
    title: "Chiến dịch làm sạch bờ biển 2025",
    excerpt:
      "Hơn 5000 tình nguyện viên đã tham gia dọn dẹp rác thải nhựa dọc bờ biển miền Trung...",
    tag: "Tin tức",
    tagClass: "tag-news", // Class riêng cho màu sắc
    image:
      "https://images.unsplash.com/photo-1621451537084-482c73073a0f?auto=format&fit=crop&w=500&q=80",
  },
  {
    id: 2,
    title: "5 cách tái chế chai nhựa cũ",
    excerpt:
      "Biến chai nhựa thành chậu cây hoặc hộp đựng bút cực xinh chỉ với vài bước đơn giản...",
    tag: "Mẹo vặt",
    tagClass: "tag-tips",
    image:
      "https://images.unsplash.com/photo-1591191853822-112e304748a2?auto=format&fit=crop&w=500&q=80",
  },
  {
    id: 3,
    title: "Sống tối giản để bảo vệ trái đất",
    excerpt:
      "Lối sống tối giản giúp giảm thiểu rác thải sinh hoạt và tiết kiệm tài nguyên như thế nào?",
    tag: "Góc nhìn",
    tagClass: "tag-view",
    image:
      "https://images.unsplash.com/photo-1542601906990-b4d3fb7d5b43?auto=format&fit=crop&w=500&q=80",
  },
];

export default function Blog() {
  return (
    <>
      <style>{styles}</style>
      <section id="blog" className="blog-section">
        <div className="blog-container">
          {/* Phần tiêu đề Section */}
          <div className="blog-header">
            <div>
              <h2 className="section-title">Blog Môi Trường</h2>
              <p className="section-subtitle">
                Cập nhật tin tức và mẹo sống xanh mới nhất.
              </p>
            </div>
            <a href="/blog" className="view-all-link">
              Xem tất cả <ArrowRight size={16} />
            </a>
          </div>

          {/* Lưới bài viết */}
          <div className="blog-grid">
            {BLOG_DATA.map((blog) => (
              <article key={blog.id} className="blog-card group">
                <div className="blog-image-wrapper">
                  <img
                    src={blog.image}
                    alt={blog.title}
                    className="blog-image"
                  />
                </div>
                <div className="blog-content">
                  <span className={`blog-tag ${blog.tagClass}`}>
                    {blog.tag}
                  </span>
                  <h3 className="blog-title">
                    <a href={`/blog/${blog.id}`}>{blog.title}</a>
                  </h3>
                  <p className="blog-excerpt">{blog.excerpt}</p>
                  <a href={`/blog/${blog.id}`} className="read-more-btn">
                    Đọc thêm <ArrowRight size={14} />
                  </a>
                </div>
              </article>
            ))}
          </div>
        </div>
      </section>
    </>
  );
}
