import React, { useState, useEffect, useRef, useCallback } from "react";
import {
  Leaf,
  Globe,
  Recycle,
  Trash2,
  Apple,
  Menu,
  X,
  Facebook,
  Instagram,
  Youtube,
  ArrowRight,
  Play,
} from "lucide-react";

// --- Constants & Data ---
const GUIDE_DATA = [
  {
    id: "organic",
    title: "R√°c H·ªØu C∆°",
    desc: "D·ªÖ ph√¢n h·ªßy, d√πng l√†m ph√¢n b√≥n.",
    color: "text-green-600",
    borderColor: "border-green-500",
    bgIcon: "bg-green-100",
    icon: <Apple size={32} />,
    items: ["Th·ª©c ƒÉn th·ª´a", "V·ªè rau c·ªß qu·∫£", "B√£ tr√†, c√† ph√™"],
  },
  {
    id: "recycle",
    title: "R√°c T√°i Ch·∫ø",
    desc: "C√≥ th·ªÉ t√°i sinh v√≤ng ƒë·ªùi m·ªõi.",
    color: "text-blue-600",
    borderColor: "border-blue-500",
    bgIcon: "bg-blue-100",
    icon: <Recycle size={32} />,
    items: ["Gi·∫•y, s√°ch b√°o", "Chai nh·ª±a s·∫°ch", "V·ªè lon kim lo·∫°i"],
  },
  {
    id: "inorganic",
    title: "R√°c V√¥ C∆° / Kh√°c",
    desc: "Kh√¥ng th·ªÉ t√°i ch·∫ø, c·∫ßn x·ª≠ l√Ω ri√™ng.",
    color: "text-orange-600",
    borderColor: "border-orange-500",
    bgIcon: "bg-orange-100",
    icon: <Trash2 size={32} />,
    items: ["T√∫i nilon b·∫©n", "S√†nh s·ª© v·ª°", "Gi·∫•y ƒÉn ƒë√£ d√πng"],
  },
];

const BLOG_DATA = [
  {
    id: 1,
    title: "Chi·∫øn d·ªãch l√†m s·∫°ch b·ªù bi·ªÉn 2025",
    excerpt: "H∆°n 5000 t√¨nh nguy·ªán vi√™n ƒë√£ tham gia d·ªçn d·∫πp r√°c th·∫£i nh·ª±a...",
    tag: "Tin t·ª©c",
    tagColor: "text-green-600 bg-green-100",
    image:
      "https://images.unsplash.com/photo-1621451537084-482c73073a0f?auto=format&fit=crop&w=500&q=80",
  },
  {
    id: 2,
    title: "5 c√°ch t√°i ch·∫ø chai nh·ª±a c≈©",
    excerpt: "Bi·∫øn chai nh·ª±a th√†nh ch·∫≠u c√¢y ho·∫∑c h·ªôp ƒë·ª±ng b√∫t c·ª±c xinh...",
    tag: "M·∫πo v·∫∑t",
    tagColor: "text-blue-600 bg-blue-100",
    image:
      "https://images.unsplash.com/photo-1591191853822-112e304748a2?auto=format&fit=crop&w=500&q=80",
  },
  {
    id: 3,
    title: "S·ªëng t·ªëi gi·∫£n ƒë·ªÉ b·∫£o v·ªá tr√°i ƒë·∫•t",
    excerpt:
      "L·ªëi s·ªëng t·ªëi gi·∫£n gi√∫p gi·∫£m thi·ªÉu r√°c th·∫£i sinh ho·∫°t nh∆∞ th·∫ø n√†o?",
    tag: "G√≥c nh√¨n",
    tagColor: "text-orange-600 bg-orange-100",
    image:
      "https://images.unsplash.com/photo-1542601906990-b4d3fb7d5b43?auto=format&fit=crop&w=500&q=80",
  },
];

// --- Game Logic Hook ---
// Custom hook ch·ª©a logic game Canvas ƒë·ªÉ c·∫≠p nh·∫≠t hi·ªáu nƒÉng t·ªët h∆°n
const useWasteGame = (canvasRef) => {
  const [gameState, setGameState] = useState("start"); // start, playing, gameover
  const [score, setScore] = useState(0);

  // D√πng ref ƒë·ªÉ l∆∞u bi·∫øn game loop m√† kh√¥ng g√¢y re-render
  const requestRef = useRef();
  const scoreRef = useRef(0);
  const binRef = useRef({ x: 250, y: 340, width: 60, height: 50, speed: 8 });
  const itemsRef = useRef([]);
  const frameCountRef = useRef(0);
  const keysPressed = useRef({});
  const touchX = useRef(null);

  const startGame = () => {
    setGameState("playing");
    setScore(0);
    scoreRef.current = 0;
    itemsRef.current = [];

    // ƒê·∫£m b·∫£o canvas ƒë√£ load tr∆∞·ªõc khi l·∫•y width
    if (canvasRef.current) {
      binRef.current.x = canvasRef.current.width / 2 - 30;
      loop();
    }
  };

  const spawnItem = (canvasWidth) => {
    // 70% r√°c t·ªët (t√°i ch·∫ø), 30% r√°c x·∫•u (v·ªè chu·ªëi/r√°c th·∫£i kh√°c)
    const type = Math.random() > 0.3 ? "good" : "bad";
    itemsRef.current.push({
      x: Math.random() * (canvasWidth - 30),
      y: -30,
      size: 30,
      speed: 3 + Math.random() * 3, // T·ªëc ƒë·ªô r∆°i ng·∫´u nhi√™n
      type: type,
      symbol: type === "good" ? "‚ôªÔ∏è" : "üçå",
    });
  };

  const updateGame = (ctx, canvas) => {
    // N·∫øu game ch∆∞a ·ªü tr·∫°ng th√°i 'playing', d·ª´ng v√≤ng l·∫∑p
    if (gameState !== "playing") return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // 1. Update Bin Position (Th√πng r√°c)
    const bin = binRef.current;

    // X·ª≠ l√Ω Input b·∫±ng b√†n ph√≠m
    if (keysPressed.current["ArrowLeft"] && bin.x > 0) bin.x -= bin.speed;
    if (keysPressed.current["ArrowRight"] && bin.x < canvas.width - bin.width)
      bin.x += bin.speed;

    // X·ª≠ l√Ω Input b·∫±ng ch·∫°m/vu·ªët m√†n h√¨nh (Touch)
    if (touchX.current !== null) {
      // Di chuy·ªÉn theo v·ªã tr√≠ ch·∫°m
      if (touchX.current < bin.x + bin.width / 2) bin.x -= bin.speed;
      if (touchX.current > bin.x + bin.width / 2) bin.x += bin.speed;
      // Gi·ªõi h·∫°n di chuy·ªÉn
      if (bin.x < 0) bin.x = 0;
      if (bin.x > canvas.width - bin.width) bin.x = canvas.width - bin.width;
    }

    // 2. Draw Bin (V·∫Ω Th√πng r√°c)
    ctx.fillStyle = "#22c55e"; // M√†u xanh l√°
    ctx.fillRect(bin.x, bin.y, bin.width, bin.height);
    ctx.font = "20px Arial";
    ctx.fillStyle = "white";
    ctx.fillText("üóëÔ∏è", bin.x + 15, bin.y + 32);

    // 3. Spawn Logic (T·∫°o R√°c)
    // T·∫°o r√°c m·ªõi m·ªói 60 frame (kho·∫£ng 1 gi√¢y)
    if (frameCountRef.current % 60 === 0) spawnItem(canvas.width);
    frameCountRef.current++;

    // 4. Update Items (C·∫≠p nh·∫≠t v√† x·ª≠ l√Ω R√°c)
    for (let i = 0; i < itemsRef.current.length; i++) {
      let item = itemsRef.current[i];
      item.y += item.speed;

      // V·∫Ω R√°c (d√πng Emoji)
      ctx.font = "24px Arial";
      ctx.fillText(item.symbol, item.x, item.y);

      // Ph√°t hi·ªán va ch·∫°m (Collision Detection)
      if (
        item.x < bin.x + bin.width &&
        item.x + item.size > bin.x &&
        item.y < bin.y + bin.height &&
        item.y + item.size > bin.y
      ) {
        // Va ch·∫°m!
        if (item.type === "good") {
          scoreRef.current += 10; // C·ªòNG ƒëi·ªÉm n·∫øu h·ª©ng r√°c t√°i ch·∫ø (‚ôªÔ∏è)
          // Th√™m √¢m thanh n·∫øu ƒë∆∞·ª£c
        } else {
          scoreRef.current -= 5; // TR·ª™ ƒëi·ªÉm n·∫øu h·ª©ng r√°c x·∫•u (üçå)
        }
        setScore(scoreRef.current); // ƒê·ªìng b·ªô ƒëi·ªÉm s·ªë v·ªõi UI
        itemsRef.current.splice(i, 1); // X√≥a r√°c ƒë√£ h·ª©ng
        i--;
      } else if (item.y > canvas.height) {
        // R√°c r∆°i ra kh·ªèi m√†n h√¨nh
        if (item.type === "good") {
          // B·ªè s√≥t r√°c t√°i ch·∫ø -> Game Over
          setGameState("gameover");
          cancelAnimationFrame(requestRef.current);
          return;
        }
        itemsRef.current.splice(i, 1); // X√≥a r√°c ƒë√£ miss (r√°c x·∫•u miss th√¨ kh√¥ng sao)
        i--;
      }
    }

    requestRef.current = requestAnimationFrame(() => updateGame(ctx, canvas));
  };

  const loop = useCallback(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext("2d");
      // Thi·∫øt l·∫≠p k√≠ch th∆∞·ªõc canvas th·ª±c t·∫ø (n·∫øu c·∫ßn responsive)
      // Hi·ªán t·∫°i d√πng k√≠ch th∆∞·ªõc c·ªë ƒë·ªãnh 600x400
      binRef.current.y = canvas.height - binRef.current.height - 10;
      updateGame(ctx, canvas);
    }
  }, [gameState]); // Th√™m gameState v√†o dependency ƒë·ªÉ useEffect d∆∞·ªõi ƒë√¢y ho·∫°t ƒë·ªông

  // X·ª≠ l√Ω V√≤ng l·∫∑p Game (Loop)
  useEffect(() => {
    if (gameState === "playing") {
      requestRef.current = requestAnimationFrame(loop);
    }
    return () => cancelAnimationFrame(requestRef.current);
  }, [gameState, loop]);

  // X·ª≠ l√Ω Input Events
  useEffect(() => {
    const handleKeyDown = (e) => (keysPressed.current[e.key] = true);
    const handleKeyUp = (e) => (keysPressed.current[e.key] = false);

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  return { gameState, score, startGame, touchX };
};

