import React, { useState } from "react";
import "./WasteClassification.css";

function WasteClassification() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  // Xử lý khi chọn ảnh
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(file);
      setPreview(URL.createObjectURL(file));
      setResult(null);
    }
  };

  // Xử lý gửi ảnh lên server Flask để phân loại
  const handlePredict = async () => {
    if (!selectedImage) return;

    setLoading(true);
    setResult(null);

    // Tạo FormData để gửi
    const formData = new FormData();
    formData.append("file", selectedImage);

    try {
      // TODO: Thay đổi URL thành API Flask của bạn
      const response = await fetch("http://localhost:5000/predict", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setResult(data); // data: { label: "Rác hữu cơ", confidence: 0.95 }
    } catch (error) {
      console.error("Lỗi dự đoán:", error);
      setResult({ label: "Lỗi server", confidence: 0 });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="waste-classification">
      <h1>Phân loại rác bằng AI</h1>
      <p>Upload ảnh rác để hệ thống nhận diện loại rác.</p>

      <div className="upload-section">
        <input type="file" accept="image/*" onChange={handleImageChange} />
        {preview && (
          <div className="preview-container">
            <img src={preview} alt="Preview" className="preview-image" />
          </div>
        )}
        <button
          className="btn-predict"
          onClick={handlePredict}
          disabled={!selectedImage || loading}
        >
          {loading ? "Đang dự đoán..." : "Dự đoán"}
        </button>
      </div>

      {result && (
        <div className="result">
          <h3>Kết quả:</h3>
          <p>
            Loại rác: <strong>{result.label}</strong>
          </p>
          <p>
            Độ tin cậy: <strong>{(result.confidence * 100).toFixed(2)}%</strong>
          </p>
        </div>
      )}
    </div>
  );
}

export default WasteClassification;
