// src/components/page/Blog/BlogDetail.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import CommentList from "./CommentList";
import "./BlogDetail.css";

const API = "http://localhost:5000";

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  // H√ÄM LOAD BLOG (COMMENT + LIKE)
  const fetchBlog = async () => {
    try {
      const res = await fetch(`${API}/api/blog/${id}`);
      const data = await res.json();
      console.log("Blog detail:", data);
      setBlog(data);
      setLoading(false);
    } catch (error) {
      console.error("L·ªói fetch chi ti·∫øt blog:", error);
      setLoading(false);
    }
  };

  // LIKE / DISLIKE BLOG
  const handleBlogReaction = async (type) => {
    if (!token) return alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p");

    await fetch(`${API}/api/blog/${id}/reaction`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ type }),
    });

    fetchBlog(); // reload l·∫°i blog sau khi like/dislike
  };

  useEffect(() => {
    fetchBlog();
  }, [id]);

  if (loading) return <p>ƒêang t·∫£i...</p>;
  if (!blog) return <p>Kh√¥ng t√¨m th·∫•y b√†i vi·∫øt</p>;

  return (
    <div className="blog-detail-container">
      <h1 className="blog-detail-title">{blog.title}</h1>

      <p className="blog-author">Vi·∫øt b·ªüi: {blog.author}</p>

      {blog.thumbnail && (
        <img
          src={`${API}/uploads/blogs/${blog.thumbnail}`}
          alt="Thumbnail"
          className="blog-thumbnail"
        />
      )}

      <div className="blog-content">{blog.content}</div>

      {/* LIKE / DISLIKE BLOG */}
      <div className="blog-reaction-box">
        <button
          className="react-btn like-btn"
          onClick={() => handleBlogReaction("like")}
        >
          üëç {blog.likes}
        </button>

        <button
          className="react-btn dislike-btn"
          onClick={() => handleBlogReaction("dislike")}
        >
          üëé {blog.dislikes}
        </button>
      </div>

      <h2 className="comment-title">B√¨nh lu·∫≠n</h2>

      <CommentList
        comments={blog.comments || []}
        blogId={id}
        reload={fetchBlog}
      />
    </div>
  );
};

export default BlogDetail;
