// src/components/page/Blog/BlogList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Blog.css";

const API = "http://localhost:5000";

export default function BlogList() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`${API}/api/blog`)
      .then((res) => res.json())
      .then((data) => {
        console.log("Blogs fetched:", data);
        // N·∫øu backend tr·∫£ v·ªÅ array (ƒë√∫ng nh∆∞ blogRoutes ƒëang l√†m)
        if (Array.isArray(data)) {
          setBlogs(data);
        } else if (Array.isArray(data.blogs)) {
          // N·∫øu sau n√†y b·∫°n ƒë·ªïi backend tr·∫£ { blogs: [...] }
          setBlogs(data.blogs);
        } else {
          setBlogs([]);
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error("Fetch blog error:", err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>ƒêang t·∫£i danh s√°ch b√†i vi·∫øt...</p>;

  return (
    <div className="bloglist-wrapper">
      <h1 className="bloglist-title">üìö B√†i vi·∫øt m·ªõi nh·∫•t</h1>

      <div className="bloglist-grid">
        {blogs.length === 0 ? (
          <p className="bloglist-empty">Ch∆∞a c√≥ b√†i vi·∫øt n√†o</p>
        ) : (
          blogs.map((blog) => (
            <Link
              to={`/blog/${blog.id}`}
              key={blog.id}
              className="bloglist-card"
            >
              <div className="bloglist-thumbnail">
                {blog.thumbnail ? (
                  <img
                    src={`${API}/uploads/blogs/${blog.thumbnail}`}
                    alt={blog.title}
                  />
                ) : (
                  <div className="bloglist-noimage">Kh√¥ng c√≥ ·∫£nh</div>
                )}
              </div>

              <div className="bloglist-info">
                <h2 className="bloglist-post-title">{blog.title}</h2>
                <p className="bloglist-author">‚úè {blog.author}</p>
                <p className="bloglist-date">
                  üìÖ {new Date(blog.createdAt).toLocaleDateString("vi-VN")}
                </p>
              </div>
            </Link>
          ))
        )}
      </div>
    </div>
  );
}
