import { BiRightArrowAlt } from "react-icons/bi";

// --- GAME LOGIC HOOK ---
// T√°ch bi·ªát logic game v√†o m·ªôt hook t√πy ch·ªânh
const useGame = (canvasRef, scoreDisplayRef, finalScoreRef, setScore) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [gameOverVisible, setGameOverVisible] = useState(false);
  const animationId = useRef(null);

  const startGame = useCallback(() => {
    setScore(0);
    setIsPlaying(true);
    setGameOverVisible(false);
    if (animationId.current) cancelAnimationFrame(animationId.current);
    animate();
  }, [setScore]);

  const gameOver = useCallback(
    (finalScore) => {
      setIsPlaying(false);
      cancelAnimationFrame(animationId.current);
      finalScoreRef.current.textContent = finalScore;
      setGameOverVisible(true);
    },
    [finalScoreRef]
  );

  const animate = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext("2d");
    const W = canvas.width;
    const H = canvas.height;

    let score = 0;
    let items = [];
    let frameCount = 0;
    const bin = {
      x: W / 2 - 30,
      y: H - 60,
      width: 60,
      height: 50,
      color: "#22c55e",
      speed: 8,
    };
    let keys = {};
    let touchX = null;

    // Cleanup function
    let cleanup = () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
      canvas.removeEventListener("touchmove", handleTouchMove);
      canvas.removeEventListener("touchend", handleTouchEnd);
    };

    const handleKeyDown = (e) => {
      keys[e.key] = true;
    };
    const handleKeyUp = (e) => {
      keys[e.key] = false;
    };
    const handleTouchMove = (e) => {
      e.preventDefault();
      const rect = canvas.getBoundingClientRect();
      touchX = e.touches[0].clientX - rect.left;
    };
    const handleTouchEnd = () => {
      touchX = null;
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    canvas.addEventListener("touchmove", handleTouchMove, { passive: false });
    canvas.addEventListener("touchend", handleTouchEnd);

    const gameLoop = () => {
      if (!isPlaying) {
        cleanup();
        return;
      }

      ctx.clearRect(0, 0, W, H);

      // Move Bin (Keyboard)
      if (keys["ArrowLeft"] && bin.x > 0) bin.x -= bin.speed;
      if (keys["ArrowRight"] && bin.x < W - bin.width) bin.x += bin.speed;

      // Move Bin (Touch)
      if (touchX !== null) {
        const targetX = touchX - bin.width / 2;
        if (targetX < bin.x) bin.x -= Math.min(bin.speed, bin.x - targetX);
        if (targetX > bin.x) bin.x += Math.min(bin.speed, targetX - bin.x);

        if (bin.x < 0) bin.x = 0;
        if (bin.x > W - bin.width) bin.x = W - bin.width;
      }

      // Draw Bin
      ctx.fillStyle = bin.color;
      ctx.fillRect(bin.x, bin.y, bin.width, bin.height);
      ctx.fillStyle = "white";
      ctx.font = "20px Arial";
      ctx.fillText("üóëÔ∏è", bin.x + 15, bin.y + 32);

      // Spawn items
      if (frameCount % 60 === 0) {
        const type = Math.random() > 0.3 ? "good" : "bad"; // 70% good, 30% bad
        items.push({
          x: Math.random() * (W - 30),
          y: -30,
          size: 30,
          speed: 3 + Math.random() * 2,
          type: type,
          symbol: type === "good" ? "‚ôªÔ∏è" : "üçå",
        });
      }
      frameCount++;

      // Update & Draw Items
      for (let i = 0; i < items.length; i++) {
        let item = items[i];
        item.y += item.speed;

        // Draw Item
        ctx.font = "24px Arial";
        ctx.fillText(item.symbol, item.x, item.y + item.size / 2);

        // Collision Detection
        if (
          item.x < bin.x + bin.width &&
          item.x + item.size > bin.x &&
          item.y + item.size > bin.y &&
          item.y < bin.y + bin.height // Fix: Use item.y instead of item.y + item.size for bottom check
        ) {
          if (item.type === "good") {
            score += 10;
          } else {
            score -= 5;
          }
          setScore(score); // C·∫≠p nh·∫≠t score trong state
          items.splice(i, 1);
          i--;
        } else if (item.y > H) {
          // Missed item
          if (item.type === "good") {
            gameOver(score);
            cleanup();
            return;
          }
          items.splice(i, 1);
          i--;
        }
      }
      animationId.current = requestAnimationFrame(gameLoop);
    };
    gameLoop();
  }, [canvasRef, isPlaying, setScore, gameOver]);

  return { startGame, isPlaying, gameOverVisible };
};

// --- MAIN CONTENT COMPONENT ---
