import React, { useState } from "react";
import "./WasteClassification.css";

function WasteClassification() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  // X·ª≠ l√Ω khi ch·ªçn ·∫£nh
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(file);
      setPreview(URL.createObjectURL(file));
      setResult(null); // Reset k·∫øt qu·∫£ khi ch·ªçn ·∫£nh m·ªõi
    }
  };

  // X·ª≠ l√Ω g·ª≠i ·∫£nh l√™n server Node.js ƒë·ªÉ ph√¢n lo·∫°i
  const handlePredict = async () => {
    if (!selectedImage) return;

    setLoading(true);
    setResult(null);

    // T·∫°o FormData ƒë·ªÉ g·ª≠i
    const formData = new FormData();
    // üî• S·ª¨A L·ªñI QUAN TR·ªåNG: T√™n tr∆∞·ªùng ph·∫£i l√† 'image' ƒë·ªÉ kh·ªõp v·ªõi Multer trong Node.js
    formData.append("image", selectedImage);

    try {
      // üî• S·ª¨A L·ªñI QUAN TR·ªåNG: URL ph·∫£i l√† http://localhost:5000/api/predict
      const API_URL = "http://localhost:5000/api/predict";

      const response = await fetch(API_URL, {
        method: "POST",
        body: formData,
      });

      // Ki·ªÉm tra l·ªói HTTP (v√≠ d·ª•: 500 Internal Server Error)
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "L·ªói d·ª± ƒëo√°n t·ª´ Server.");
      }

      const data = await response.json();
      // Server Node.js tr·∫£ v·ªÅ data.prediction: { label: "...", confidence: ... }
      setResult(data.prediction);
    } catch (error) {
      console.error("L·ªói d·ª± ƒëo√°n:", error);
      setResult({
        label: `L·ªói Server: ${error.message || "Kh√¥ng x√°c ƒë·ªãnh"}`,
        confidence: 0,
      });
    } finally {
      setLoading(false);
    }
  };

  // H√†m ƒë·ªãnh d·∫°ng k·∫øt qu·∫£ hi·ªÉn th·ªã (c√≥ th·ªÉ b·ªè qua n·∫øu b·∫°n d√πng CSS)
  const formatConfidence = (conf) => {
    return (conf * 100).toFixed(2) + "%";
  };

  return (
    <div className="waste-classification">
      <h1>Ph√¢n lo·∫°i r√°c b·∫±ng AI</h1>
      <p>Upload ·∫£nh r√°c ƒë·ªÉ h·ªá th·ªëng nh·∫≠n di·ªán lo·∫°i r√°c.</p>

      <div className="upload-section">
        <input type="file" accept="image/*" onChange={handleImageChange} />
        {preview && (
          <div className="preview-container">
            <img src={preview} alt="Preview" className="preview-image" />
          </div>
        )}
        <button
          className="btn-predict"
          onClick={handlePredict}
          disabled={!selectedImage || loading}
        >
          {loading ? "ƒêang d·ª± ƒëo√°n..." : "D·ª± ƒëo√°n"}
        </button>
      </div>

      {result && (
        <div className="result">
          <h3>K·∫øt qu·∫£:</h3>
          <p>
            Lo·∫°i r√°c: <strong>{result.label}</strong>
          </p>
          <p>
            ƒê·ªô tin c·∫≠y: <strong>{formatConfidence(result.confidence)}</strong>
          </p>
        </div>
      )}
    </div>
  );
}

export default WasteClassification;
