import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [comment, setComment] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetch(`http://localhost:5000/api/blog/${id}`)
      .then((res) => res.json())
      .then((data) => setBlog(data))
      .catch((err) => console.error(err));
  }, [id]);

  if (!blog) return <p>Đang tải bài viết...</p>;

  return (
    <div className="blog-detail-container">
      <h1>{blog.title}</h1>

      {blog.thumbnail && (
        <img
          src={`http://localhost:5000/uploads/blogs/${blog.thumbnail}`}
          alt=""
          className="blog-detail-thumb"
        />
      )}

      <p className="blog-content">{blog.content}</p>

      <hr />

      <h2>Bình luận</h2>

      {blog.comments.length === 0 && <p>Chưa có bình luận nào.</p>}

      <ul className="comment-list">
        {blog.comments.map((c) => (
          <li key={c.id}>
            <img
              src={`http://localhost:5000/uploads/avatars/${c.avatar}`}
              alt=""
              className="comment-avatar"
            />
            <div>
              <strong>{c.userName}</strong>
              <p>{c.content}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BlogDetail;
