import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./Login.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logic xử lý đăng nhập sẽ được thêm vào đây
    console.log("Đăng nhập với:", { email, password });
    alert(`Đăng nhập thành công (giả lập) với email: ${email}`);
    // Sau khi xử lý, có thể chuyển hướng người dùng
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h2 className="login-title">Đăng nhập</h2>
        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Nhập email của bạn"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Mật khẩu</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Nhập mật khẩu"
              required
            />
            <div className="forgot-password">
              <NavLink to="/forgot-password">Quên mật khẩu?</NavLink>
            </div>
          </div>

          <button type="submit" className="login-button">
            Đăng nhập
          </button>
        </form>

        <p className="register-link">
          Chưa có tài khoản? <NavLink to="/register">Đăng ký ngay</NavLink>
        </p>
      </div>
    </div>
  );
}

export default Login;
