    description: "D·ªÖ ph√¢n h·ªßy, d√πng l√†m ph√¢n b√≥n.",
    items: ["Th·ª©c ƒÉn th·ª´a", "V·ªè rau c·ªß qu·∫£", "B√£ tr√†, c√† ph√™"],
    icon: "üçÇ",
    borderColor: "#22c55e",
    iconBg: "#dcfce7",
  },
  {
    title: "R√°c T√°i Ch·∫ø",
    description: "C√≥ th·ªÉ t√°i sinh v√≤ng ƒë·ªùi m·ªõi.",
    items: ["Gi·∫•y, s√°ch b√°o", "Chai nh·ª±a s·∫°ch", "V·ªè lon kim lo·∫°i"],
    icon: "‚ôªÔ∏è",
    borderColor: "#3b82f6",
    iconBg: "#dbeafe",
  },
  {
    title: "R√°c V√¥ C∆° / Kh√°c",
    description: "Kh√¥ng th·ªÉ t√°i ch·∫ø, c·∫ßn x·ª≠ l√Ω ri√™ng.",
    items: ["T√∫i nilon b·∫©n", "S√†nh s·ª© v·ª°", "Gi·∫•y ƒÉn ƒë√£ d√πng"],
    icon: "üóëÔ∏è",
    borderColor: "#f97316",
    iconBg: "#ffedd5",
  },
];

const blogData = [
  {
    title: "Chi·∫øn d·ªãch l√†m s·∫°ch b·ªù bi·ªÉn 2025",
    description:
      "H∆°n 5000 t√¨nh nguy·ªán vi√™n ƒë√£ tham gia d·ªçn d·∫πp r√°c th·∫£i nh·ª±a...",
    link: "#",
  },
  {
    title: "5 c√°ch t√°i ch·∫ø chai nh·ª±a c≈©",
    description: "Bi·∫øn chai nh·ª±a th√†nh ch·∫≠u c√¢y ho·∫∑c h·ªôp ƒë·ª±ng b√∫t c·ª±c xinh...",
    link: "#",
  },
  {
    title: "S·ªëng t·ªëi gi·∫£n ƒë·ªÉ b·∫£o v·ªá tr√°i ƒë·∫•t",
    description: "L·ªëi s·ªëng t·ªëi gi·∫£n gi√∫p gi·∫£m thi·ªÉu r√°c th·∫£i sinh ho·∫°t...",
    link: "#",
  },
];

function GuideGamePage() {
  const canvasRef = useRef(null);
  const [score, setScore] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  // Game state
  const [bin, setBin] = useState({ x: 250, y: 340, width: 60, height: 50 });
  const [items, setItems] = useState([]);
  const keys = useRef({});
  const frameCount = useRef(0);
  const animationId = useRef(null);

  useEffect(() => {
    const handleKeyDown = (e) => (keys.current[e.key] = true);
    const handleKeyUp = (e) => (keys.current[e.key] = false);
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  const startGame = () => {
    setScore(0);
    setItems([]);
    setIsPlaying(true);
    frameCount.current = 0;
    animate();
  };

  const gameOver = () => {
    setIsPlaying(false);
    cancelAnimationFrame(animationId.current);
  };

  const spawnItem = () => {
    const type = Math.random() > 0.3 ? "good" : "bad";
    setItems((prev) => [
      ...prev,
      {
        x: Math.random() * (canvasRef.current.width - 30),
        y: -30,
        size: 30,
        speed: 3 + Math.random() * 3,
        type,
        symbol: type === "good" ? "‚ôªÔ∏è" : "üçå",
      },
    ]);
  };

  const animate = () => {
    if (!isPlaying) return;
    const ctx = canvasRef.current.getContext("2d");
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    // Move bin
    setBin((prev) => {
      let newX = prev.x;
      if (keys.current["ArrowLeft"] && newX > 0) newX -= 8;
      if (
        keys.current["ArrowRight"] &&
        newX < canvasRef.current.width - prev.width
      )
        newX += 8;
      return { ...prev, x: newX };
    });

    // Draw bin
    ctx.fillStyle = "#22c55e";
    ctx.fillRect(bin.x, bin.y, bin.width, bin.height);
    ctx.font = "20px Arial";
    ctx.fillText("üóëÔ∏è", bin.x + 15, bin.y + 32);

    // Spawn items
    if (frameCount.current % 60 === 0) spawnItem();
    frameCount.current++;

    // Draw items
    const updatedItems = [];
    items.forEach((item) => {
      item.y += item.speed;
      ctx.font = "24px Arial";
      ctx.fillText(item.symbol, item.x, item.y);

      // Collision
      if (
        item.x < bin.x + bin.width &&
        item.x + item.size > bin.x &&
        item.y < bin.y + bin.height &&
        item.y + item.size > bin.y
      ) {
        if (item.type === "good") setScore((prev) => prev + 10);
        else setScore((prev) => prev - 5);
      } else if (item.y < canvasRef.current.height) {
        updatedItems.push(item);
      }
    });
    setItems(updatedItems);

    animationId.current = requestAnimationFrame(animate);
  };

  return (
    <div className="guide-game-page">
      {/* H∆∞·ªõng d·∫´n */}
      <section className="guide-section">
        <h2>H∆∞·ªõng D·∫´n Ph√¢n Lo·∫°i R√°c</h2>
        <div className="guide-cards">
          {guideData.map((g, i) => (
            <div
              className="guide-card"
              key={i}
              style={{ borderTopColor: g.borderColor }}
            >
              <div className="guide-icon" style={{ backgroundColor: g.iconBg }}>
                {g.icon}
              </div>
              <h3>{g.title}</h3>
              <p>{g.description}</p>
              <ul>
                {g.items.map((item, idx) => (
                  <li key={idx}>‚Ä¢ {item}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </section>

      {/* Blog */}
      <section className="blog-section">
        <h2>Blog M√¥i Tr∆∞·ªùng</h2>
        <div className="blog-cards">
          {blogData.map((b, i) => (
            <div className="blog-card" key={i}>
              <h3>{b.title}</h3>
              <p>{b.description}</p>
              <a href={b.link}>ƒê·ªçc th√™m ‚Üí</a>
            </div>
          ))}
        </div>
      </section>

      {/* Game */}
      <section className="game-section">
        <h2>Mini Game: H·ª©ng R√°c</h2>
        <p>ƒêi·ªÉm: {score}</p>
        {!isPlaying && (
          <button className="start-btn" onClick={startGame}>
            B·∫Øt ƒë·∫ßu
          </button>
        )}
        <canvas
          ref={canvasRef}
          width={600}
          height={400}
          className="game-canvas"
        ></canvas>
      </section>
    </div>
  );
}

export default GuideGamePage;
